INSERT INTO `wp_7h10cshrbk_posts` VALUES(247, 3, '2015-02-23 07:03:43', '2015-02-23 03:03:43', '<p>[vc_row][vc_column width="1/1"]Кроме навыков английского, я принимаю во внимание жизненную ситуацию и возможности своих учеников. Чтобы занятия были эффективными, они не должны быть слишком сложными, но и не должны быть слишком простыми; если заниматься редко, толку будет мало, но и каждый день заниматься будет чересчур - вы просто не сможете усваивать материал и качественно выполнять домашние задания. Мне не интересно "отчитать" как можно больше часов, мне важен результат и, исходя из этих соображений, я буду предлагать наиболее эффективное расписания. Конечно последнее слово за вами. [/vc_column][/vc_row]</p>\n', 'Баланс', '', 'inherit', 'open', 'open', '', '244-autosave-v1', '', '', '2015-02-23 07:03:43', '2015-02-23 03:03:43', '', 244, 'http://nataliatitova.me/244-autosave-v1/', 0, 'revision', '', 0);
