INSERT INTO `wp_7h10cshrbk_posts` VALUES(315, 1, '2015-03-02 05:01:14', '2015-03-02 01:01:14', '[vc_row margin_top="60" margin_bottom="-40" padding_left="40" padding_right="40" bg_position="top" bg_repeat="no-repeat" bg_cover="false" bg_attachment="false" padding_top="40" padding_bottom="40" parallax_speed="0.1" full_width="" enable_parallax="" bg_type="no_bg" bg_grad="background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #E3E3E3));background: -moz-linear-gradient(top,#E3E3E3 0%);background: -webkit-linear-gradient(top,#E3E3E3 0%);background: -o-linear-gradient(top,#E3E3E3 0%);background: -ms-linear-gradient(top,#E3E3E3 0%);background: linear-gradient(top,#E3E3E3 0%);" parallax_style="vcpb-default" bg_image_repeat="repeat" bg_image_size="cover" bg_img_attach="scroll" parallax_sense="30" animation_direction="left-animation" animation_repeat="repeat" video_opts="" viewport_vdo="off" enable_controls="off" bg_override="0" parallax_content_sense="30" fadeout_start_effect="30" overlay_pattern_opacity="80" overlay_pattern_attachment="fixed" multi_color_overlay="" multi_color_overlay_opacity="60" seperator_type="none_seperator" seperator_position="top_seperator" seperator_shape_size="40" seperator_svg_height="60" seperator_shape_background="#ffffff" seperator_shape_border="none" seperator_shape_border_width="1" icon_type="no_icon" icon_size="32" icon_style="none" icon_color_border="#333333" icon_border_size="1" icon_border_radius="500" icon_border_spacing="50" img_width="48" ult_hide_row_large_screen="off" ult_hide_row_desktop="off" ult_hide_row_tablet="off" ult_hide_row_tablet_small="off" ult_hide_row_mobile="off" ult_hide_row_mobile_large="off"][vc_column width="1/6"][vc_empty_space height="32px"][/vc_column][vc_column width="2/3"][vc_empty_space height="45px"][vc_column_text]\r\n<h2 style="text-align: center;">В ЧЕМ УНИКАЛЬНОСТЬ МОЕГО ПОДХОДА?</h2>\r\n[/vc_column_text][vc_empty_space height="50px"][vc_column_text]\r\n<p style="text-align: center; font-size: 20px; line-height: 30px;">В изучении английского, да и в образовании в целом очень плохо работает метод "всех под одну гребенку". Каждый человек уникален, у всех разные цели, и чем более специализирован план обучения, тем более он эффективен. Мои уроки персонифицированы, сбалансированы и интересны. Вы изучите то, что именно ВАМ нужно, вы не будете ошарашены потоком информации, но и расслабляться тоже не получится!</p>\r\n\r\n[/vc_column_text][vc_empty_space height="32px"][dt_benefits_vc column_width="240" columns_number="1" style="3" image_background="false" image_background_size="70" icons_size="50" icons_paint="accent" icons_hover_paint="accent" icons_hover_color="#dddddd" decorative_lines="disabled" header_size="h3" content_size="big" number="6" target_blank="false" orderby="date" order="asc" animation="zoomIn" animate="at_the_same_time" image_background_paint="accent" image_hover_background_paint="accent" icons_color="#ffffff" image_background_color="#222222" image_hover_background_color="#444444" category="method_ru"][vc_empty_space height="60px"][/vc_column][vc_column width="1/6"][vc_empty_space height="32px"][/vc_column][/vc_row][vc_row][vc_column][vc_column_text]\r\n[/vc_column_text][/vc_column][/vc_row][vc_row bg_position="top" bg_repeat="no-repeat" bg_cover="true" bg_attachment="true" padding_top="60" padding_bottom="60" margin_top="0" margin_bottom="0" padding_left="0" padding_right="0" parallax_speed="0.4" full_width="true" enable_parallax="false" bg_type="no_bg" bg_grad="background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #E3E3E3));background: -moz-linear-gradient(top,#E3E3E3 0%);background: -webkit-linear-gradient(top,#E3E3E3 0%);background: -o-linear-gradient(top,#E3E3E3 0%);background: -ms-linear-gradient(top,#E3E3E3 0%);background: linear-gradient(top,#E3E3E3 0%);" parallax_style="vcpb-vz-jquery" bg_image_repeat="no-repeat" bg_image_size="cover" bg_img_attach="fixed" parallax_sense="30" animation_direction="left-animat